while (index < len(datastream_buffer)) and not found:
    chars_list = datastream_buffer[index-3:index+1]
    chars_set = set(chars_list)

    if len(chars_set) != 4:
        index = index + 1
    else:
        found = True
        print(chars_set)

if found:
    print("Found at position: {}".format(index + 1))
else:
    print("Error #1 - Not found")

